 17	type GormRepository[T any] struct {
 18		Repository[T]
 19		DB *gorm.DB
 20	}
 21	
 22	func WithRelations(relations ...string) Option {
 23		return func(db *gorm.DB) *gorm.DB {
 24			for _, relation := range relations {
 25				db = db.Preload(relation)
 26			}
 27			return db
 28		}
 29	}
 30	
 31	func applyOptions(db *gorm.DB, options []Option) *gorm.DB {
 32		for _, option := range options {
 33			if option != nil {
 34				db = option(db)
 35			}
 36		}
 37		return db
 38	}
 39	
 40	func newEntity[T any]() T {
 41		var entity T
 42		entityType := reflect.TypeOf(entity)
 43		if entityType.Kind() == reflect.Ptr {
 44			return reflect.New(entityType.Elem()).Interface().(T)
 45		}
 46		return entity
 47	}
