 235	func (r *GormRepository[T]) DeleteById(ctx context.Context, id uuid.UUID, options ...Option) error {
 236		db := applyOptions(r.DB, options).WithContext(ctx)
 237		return db.Delete(new(T), "id = ?", id).Error
 238	}
