 240	func (r *GormRepository[T]) AppendAssociation(ctx context.Context, entity T, association string, values interface{}, options ...Option) error {
 241		return applyOptions(r.DB, options).
 242			WithContext(ctx).
 243			Model(&entity).
 244			Omit(association + ".*"). // https://gorm.io/docs/associations.html#Using-Omit-to-Exclude-Fields-or-Associations
 245			Association(association).
 246			Append(values)
 247	}
