 270	// BeginTransaction starts a new transaction that should be used with defer for automatic cleanup
 271	func (r *GormRepository[T]) BeginTransaction() *Tx {
 272		gtx := r.DB.Begin()
 273		return &Tx{
 274			gtx: gtx,
 275			committed: false,
 276			rolledBack: false,
 277			clonedEntities: make(map[string]interface{}),
 278		}
 279	}
 280	
 281	// WithTx returns an option to run the query within a transaction.
 282	// When used with Find operations, it automatically clones entities that support cloning.
 283	func WithTx(tx *Tx) Option {
 284		return func(db *gorm.DB) *gorm.DB {
 285			// Store the transaction reference in the context for later use
 286			return tx.gtx.Set(txContextKey, tx)
 287		}
 288	}
 289	
 290	// WithQuery returns an option to customize the query.
 291	func WithQuery(fn func(*gorm.DB) *gorm.DB) Option {
 292		return func(db *gorm.DB) *gorm.DB {
 293			return fn(db)
 294		}
 295	}
 296	
 297	func WithQueryStruct(query map[string]interface{}) Option {
 298		return func(db *gorm.DB) *gorm.DB {
 299			return db.Where(query)
 300		}
 301	}
 302	
 303	type Tx struct {
 304		gtx *gorm.DB
 305		committed bool
 306		rolledBack bool
 307		// clonedEntities stores cloned entities as snapshots during transaction
 308		// key is a unique identifier for the entity, value is the cloned entity snapshot
 309		clonedEntities map[string]interface{}
 310		mutex sync.RWMutex
 311	}
...
 349	
 350	// Finish should be called with defer to automatically handle commit/rollback
 351	// Usage: defer tx.Finish(&err)
 352	// Use this for simple cases where you don't need complex error handling
 353	// Will commit if err is nil, rollback if err is set
 354	func (tx *Tx) Finish(err *error) {
 355		if tx.committed || tx.rolledBack {
 356			return
 357		}
 358	
 359		if *err != nil {
 360			// If there was an error, rollback
 361			if rollbackErr := tx.Rollback(); rollbackErr != nil {
 362				// Log rollback error but don't override the original error
 363				// You might want to use your logging framework here
 364			}
 365		} else {
 366			// If no error, commit
 367			if commitErr := tx.Commit(); commitErr != nil {
 368				*err = commitErr
 369			}
 370		}
 371	}
 372	
 373	// Error returns any error from the underlying GORM transaction
 374	func (tx *Tx) Error() error {
 375		return tx.gtx.Error
 376	}
