 28	// Small field count optimization
 29	const smallFieldCount = 4
 30	
 31	// EntityToMap converts an entity into a map based on specified fields.
 32	func EntityToMap(fields map[string]interface{}, entity interface{}) (map[string]interface{}, error) {
 33		entityValue := reflect.Indirect(reflect.ValueOf(entity))
 34		entityType := entityValue.Type()
 35	
 36		// Get or create the field info mapping
 37		fieldInfoMap := getFieldInfoMap(entityType)
 38	
 39		// Create the update map with exact capacity
 40		updateMap := make(map[string]interface{}, len(fields))
 41	
 42		// Fast path for small field counts (common case)
 43		if len(fields) <= smallFieldCount {
 44			// Process each field
 45			for key, value := range fields {
 46				info, found := fieldInfoMap[key]
 47				if !found {
 48					return nil, errors.New("field not found in entity: " + key)
 49				}
 50	
 51				// Get field by index instead of by name (much faster)
 52				fieldValue := entityValue.Field(info.Index)
 53	
 54				// Handle pointer types
 55				if info.IsPtr {
 56					if fieldValue.IsNil() {
 57						return nil, errors.New("nil pointer encountered for field: " + key)
 58					}
 59					fieldValue = fieldValue.Elem()
 60				}
...
 81	
 82		// Regular path for larger field counts
 83		for key, value := range fields {
 84			info, found := fieldInfoMap[key]
 85			if !found {
 86				return nil, errors.New("field not found in entity: " + key)
 87			}
 88	
 89			// Get field by index instead of by name
 90			fieldValue := entityValue.Field(info.Index)
 91	
 92			if info.IsPtr {
 93				if fieldValue.IsNil() {
 94					return nil, errors.New("nil pointer encountered for field: " + key)
 95				}
 96				fieldValue = fieldValue.Elem()
 97			}
 98	
 99			if subMap, ok := value.(map[string]interface{}); ok {
 100				subUpdateMap, err := handleNestedFields(fieldValue, subMap)
 101				if err != nil {
 102					return nil, err
 103				}
 104	
 105				jsonValue, err := json.Marshal(subUpdateMap)
 106				if err != nil {
 107					return nil, err
 108				}
 109	
 110				updateMap[info.ColumnName] = gorm.Expr("? || ?", clause.Column{Name: info.ColumnName}, string(jsonValue))
 111			} else {
 112				updateMap[info.ColumnName] = fieldValue.Interface()
 113			}
 114		}
 115	
 116		return updateMap, nil
 117	}
 118	
 119	// Cache for column names
 120	var columnNameCache sync.Map
...
 132	
 133		// Not in cache, create mapping
 134		fieldMap = make(map[string]fieldInfo, entityType.NumField())
 135		namingStrategy := orm.CamelCaseNamingStrategy{}
 136	
 137		// Add each field to the mapping
 138		for i := 0; i < entityType.NumField(); i++ {
 139			field := entityType.Field(i)
 140	
 141			// Skip unexported fields
 142			if field.PkgPath != "" {
 143				continue
 144			}
 145	
 146			// Calculate column name directly
 147			columnName := namingStrategy.ColumnName("", field.Name)
 148	
 149			fieldMap[field.Name] = fieldInfo{
 150				ColumnName: columnName,
 151				Index: i,
 152				IsPtr: field.Type.Kind() == reflect.Ptr,
 153			}
 154		}
 155	
 156		// Store in cache with write lock
 157		typeCacheMutex.Lock()
 158		// Double check if another goroutine already created the mapping
 159		if existing, found := fieldInfoCache[entityType]; found {
 160			typeCacheMutex.Unlock()
 161			return existing
 162		}
 163		fieldInfoCache[entityType] = fieldMap
 164		typeCacheMutex.Unlock()
 165	
 166		return fieldMap
 167	}
