 169	// Enhanced field info cache for nested fields
 170	type nestedFieldInfo struct {
 171		Index int
 172		JSONName string
 173		IsPtr bool
 174	}
 175	
 176	// Cache for nested field access
 177	var nestedFieldCache sync.Map // map[reflect.Type]map[string]nestedFieldInfo
 178	
 179	// handleNestedFields processes nested field structures
 180	func handleNestedFields(fieldValue reflect.Value, subMap map[string]interface{}) (map[string]interface{}, error) {
 181		subUpdateMap := make(map[string]interface{}, len(subMap))
 182	
 183		switch fieldValue.Kind() {
 184		case reflect.Struct:
 185			fieldType := fieldValue.Type()
 186	
 187			// Get or create nested field info
 188			var nestedFields map[string]nestedFieldInfo
 189	
 190			// Check cache first
 191			if cached, found := nestedFieldCache.Load(fieldType); found {
 192				nestedFields = cached.(map[string]nestedFieldInfo)
 193			} else {
 194				// Create new mapping
 195				nestedFields = make(map[string]nestedFieldInfo, fieldType.NumField())
 196	
 197				for i := 0; i < fieldType.NumField(); i++ {
 198					field := fieldType.Field(i)
 199	
 200					// Skip unexported
 201					if field.PkgPath != "" {
 202						continue
 203					}
 204	
 205					jsonName := getJSONName(field)
 206					nestedFields[field.Name] = nestedFieldInfo{
 207						Index: i,
 208						JSONName: jsonName,
 209						IsPtr: field.Type.Kind() == reflect.Ptr,
 210					}
 211				}
 212	
 213				// Store in cache
 214				nestedFieldCache.Store(fieldType, nestedFields)
 215			}
 216	
 217			for subKey, subValue := range subMap {
 218				info, found := nestedFields[subKey]
 219				if !found {
 220					return nil, errors.New("field not found: " + subKey)
 221				}
 222	
 223				// Access field by index (faster than FieldByName)
 224				subFieldValue := fieldValue.Field(info.Index)
 225	
 226				// Handle pointer types
 227				if info.IsPtr {
 228					if subFieldValue.IsNil() {
 229						return nil, errors.New("nil pointer for field: " + subKey)
 230					}
 231					subFieldValue = subFieldValue.Elem()
 232				}
 233	
 234				// Handle nested maps recursively
 235				if nestedMap, ok := subValue.(map[string]interface{}); ok {
 236					nestedResult, err := handleNestedFields(subFieldValue, nestedMap)
 237					if err != nil {
 238						return nil, err
 239					}
 240					subUpdateMap[info.JSONName] = nestedResult
 241				} else {
 242					subUpdateMap[info.JSONName] = subFieldValue.Interface()
 243				}
 244			}
 245	
 246		case reflect.Map:
 247			for subKey, subValue := range subMap {
 248				keyValue := reflect.ValueOf(subKey)
 249				mapValue := fieldValue.MapIndex(keyValue)
 250	
 251				if mapValue.IsValid() {
 252					if nestedMap, ok := subValue.(map[string]interface{}); ok && mapValue.IsValid() {
 253						nestedResult, err := handleNestedFields(mapValue, nestedMap)
 254						if err != nil {
 255							return nil, err
 256						}
 257						subUpdateMap[subKey] = nestedResult
 258					} else {
 259						subUpdateMap[subKey] = mapValue.Interface()
 260					}
 261				} else {
 262					subUpdateMap[subKey] = nil
 263				}
 264			}
 265	
 266		default:
 267			return nil, errors.New("unsupported type for nested fields")
 268		}
 269	
 270		return subUpdateMap, nil
 271	}
 272	
 273	// getJSONName extracts the JSON field name from struct tags with caching
 274	func getJSONName(field reflect.StructField) string {
 275		// Use unique key based on package path, struct and field name
 276		cacheKey := field.PkgPath + "." + field.Name
 277	
 278		// Check cache first
 279		if cachedName, ok := jsonNameCache.Load(cacheKey); ok {
 280			return cachedName.(string)
 281		}
 282	
 283		// Calculate JSON name
 284		tag := field.Tag.Get("json")
 285		var result string
 286	
 287		if tag == "" || tag == "-" {
 288			result = field.Name
 289		} else {
 290			// Get the part before the first comma
 291			if idx := strings.IndexByte(tag, ','); idx != -1 {
 292				tag = tag[:idx]
 293			}
 294	
 295			if tag == "" {
 296				result = field.Name
 297			} else {
 298				result = tag
 299			}
 300		}
 301	
 302		// Store in cache
 303		jsonNameCache.Store(cacheKey, result)
 304		return result
 305	}
