 164	func NewTestPostBuilder(userID uuid.UUID) *TestPostBuilder {
 165		return &TestPostBuilder{
 166			post: TestPost{
 167				ID: uuid.New(),
 168				UserID: userID,
 169				Title: "Test Post",
 170				Content: "Test content",
