 213	// AssertUserEqual compares two users and reports differences
 214	func AssertUserEqual(t *testing.T, expected, actual TestUser, message string) {
 215		t.Helper()
 216	
 217		if expected.ID != actual.ID {
 218			t.Errorf("%s: ID mismatch - expected %v, got %v", message, expected.ID, actual.ID)
 219		}
 220		if expected.Name != actual.Name {
 221			t.Errorf("%s: Name mismatch - expected %s, got %s", message, expected.Name, actual.Name)
 222		}
 223		if expected.Email != actual.Email {
 224			t.Errorf("%s: Email mismatch - expected %s, got %s", message, expected.Email, actual.Email)
 225		}
 226		if expected.Age != actual.Age {
 227			t.Errorf("%s: Age mismatch - expected %d, got %d", message, expected.Age, actual.Age)
 228		}
 229		if expected.Active != actual.Active {
 230			t.Errorf("%s: Active mismatch - expected %t, got %t", message, expected.Active, actual.Active)
 231		}
 232	}
 233	
 234	// CreateTestUsers creates multiple test users in the database
 235	func CreateTestUsers(t *testing.T, repo *GormRepository[TestUser], count int) []TestUser {
 236		t.Helper()
 237		ctx := context.Background()
 238		users := make([]TestUser, count)
 239	
 240		for i := 0; i < count; i++ {
 241			user := NewTestUserBuilder().
 242				WithName("Test User " + string(rune(i+'1'))).
 243				WithEmail("user" + string(rune(i+'1')) + "@example.com").
 244				WithAge(20 + i).
 245				WithActive(i%2 == 0).
 246				Build()
 247	
 248			err := repo.Create(ctx, user)
 249			if err != nil {
 250				t.Fatalf("Failed to create test user %d: %v", i, err)
 251			}
 252			users[i] = user
 253		}
 254	
 255		return users
 256	}
 257	
 258	// CleanupTestDB removes all data from test tables
 259	func CleanupTestDB(t *testing.T, db *gorm.DB) {
 260		t.Helper()
 261	
 262		// Delete in reverse order of dependencies
 263		db.Exec("DELETE FROM post_tags")
 264		db.Exec("DELETE FROM test_posts")
 265		db.Exec("DELETE FROM test_tags")
 266		db.Exec("DELETE FROM test_profiles")
 267		db.Exec("DELETE FROM test_users")
 268		db.Exec("DELETE FROM test_simple_entities")
 269	}
