 271	// AssertPaginationResult validates pagination result structure
 272	func AssertPaginationResult(t *testing.T, result *PaginationResult[TestUser], expectedTotal int64, expectedPage int, expectedPageSize int, message string) {
 273		t.Helper()
 274	
 275		if result.Total != expectedTotal {
 276			t.Errorf("%s: Total mismatch - expected %d, got %d", message, expectedTotal, result.Total)
 277		}
 278		if result.CurrentPage != expectedPage {
 279			t.Errorf("%s: CurrentPage mismatch - expected %d, got %d", message, expectedPage, result.CurrentPage)
 280		}
 281		if result.Limit != expectedPageSize {
 282			t.Errorf("%s: Limit mismatch - expected %d, got %d", message, expectedPageSize, result.Limit)
 283		}
 284	
 285		expectedLastPage := int((expectedTotal + int64(expectedPageSize) - 1) / int64(expectedPageSize))
 286		if expectedTotal == 0 {
 287			expectedLastPage = 0
 288		}
 289		if result.LastPage != expectedLastPage {
 290			t.Errorf("%s: LastPage mismatch - expected %d, got %d", message, expectedLastPage, result.LastPage)
 291		}
 292	}
