 85			t.Errorf("Expected profile bio %s, got %s", profile.Bio, foundUser.Profile.Bio)
 86		}
 87	
 88		// Update user using Save
 89		foundUser.Age = 30
 90		foundUser.Name = "Updated Integration User"
 91	
 92		err = userRepo.Save(ctx, foundUser)
 93		if err != nil {
 94			t.Fatalf("Failed to save updated user: %v", err)
 95		}
