 267	func TestIntegration_AssociationManagement(t *testing.T) {
 268		db := setupIntegrationDB(t)
 269		userRepo := &GormRepository[TestUser]{DB: db}
 270		postRepo := &GormRepository[TestPost]{DB: db}
 271		tagRepo := &GormRepository[TestTag]{DB: db}
 272		ctx := context.Background()
 273	
 274		// Create user
 275		user := TestUser{
 276			ID: uuid.New(),
 277			Name: "Association Test User",
 278			Email: "associations@example.com",
 279			Age: 30,
 280			Active: true,
 281		}
 282		err := userRepo.Create(ctx, user)
 283		if err != nil {
 284			t.Fatalf("Failed to create user: %v", err)
 285		}
 286	
 287		// Create tags
 288		tag1 := TestTag{ID: uuid.New(), Name: "Go"}
 289		tag2 := TestTag{ID: uuid.New(), Name: "Testing"}
 290	
 291		err = tagRepo.Create(ctx, tag1)
 292		if err != nil {
 293			t.Fatalf("Failed to create tag1: %v", err)
 294		}
 295		err = tagRepo.Create(ctx, tag2)
 296		if err != nil {
 297			t.Fatalf("Failed to create tag2: %v", err)
 298		}
 299	
 300		// Create post
 301		post := TestPost{
 302			ID: uuid.New(),
 303			UserID: user.ID,
 304			Title: "Test Post",
 305			Content: "This is a test post content",
 306			Published: true,
 307		}
 308		err = postRepo.Create(ctx, post)
 309		if err != nil {
 310			t.Fatalf("Failed to create post: %v", err)
 311		}
 312	
 313		// Test association append
 314		err = postRepo.AppendAssociation(ctx, post, "Tags", []TestTag{tag1, tag2})
 315		if err != nil {
 316			t.Fatalf("Failed to append tags to post: %v", err)
 317		}
 318	
 319		// Verify associations were created
 320		foundPost, err := postRepo.FindById(ctx, post.ID, WithRelations("Tags"))
 321		if err != nil {
 322			t.Fatalf("Failed to find post with tags: %v", err)
 323		}
 324	
 325		if len(foundPost.Tags) != 2 {
 326			t.Errorf("Expected 2 tags on post, got %d", len(foundPost.Tags))
 327		}
 328	}
