 374	func TestGormRepository_Transaction_Rollback(t *testing.T) {
 375		db := setupTestDB(t)
 376		repo := &GormRepository[TestUser]{DB: db}
 377		ctx := context.Background()
 378	
 379		tx := repo.BeginTransaction()
 380	
 381		user := createTestUser()
 382		err := repo.Create(ctx, user, WithTx(tx))
 383		if err != nil {
 384			t.Errorf("Create in transaction failed: %v", err)
 385		}
 386	
 387		// Rollback the transaction
 388		err = tx.Rollback()
 389		if err != nil {
 390			t.Errorf("Transaction rollback failed: %v", err)
 391		}
 392	
 393		// Verify the user was not created
 394		var count int64
 395		db.Model(&TestUser{}).Count(&count)
 396		if count != tests.TestSimpleEntity {
 397			t.Errorf("Expected tests.TestSimpleEntity users after rollback, got %d", count)
 398		}
 399	}
