 16	// setupTestDB creates an in-memory SQLite database for testing
 17	func setupTestDB(t *testing.T) *gorm.DB {
 18		db, err := gorm.Open(sqlite.Open(":memory:"), &gorm.Config{
 19			Logger: logger.Default.LogMode(logger.Silent),
 20		})
 21		if err != nil {
 22			t.Fatalf("Failed to connect to test database: %v", err)
 23		}
 24	
 25		// Auto-migrate test models
 26		err = db.AutoMigrate(&tests.tests.TestUser{}, &tests.tests.TestProfile{}, &tests.tests.TestPost{}, &tests.tests.TestTag{}, &tests.tests.TestSimpleEntity{})
 27		if err != nil {
 28			t.Fatalf("Failed to migrate test models: %v", err)
 29		}
 30	
 31		return db
 32	}
