 242	func TestGormRepository_WithRelations(t *testing.T) {
 243		db := setupTestDB(t)
 244		repo := &GormRepository[tests.TestUser]{DB: db}
 245		ctx := context.Background()
 246	
 247		// Create user with profile
 248		user := createTestUser()
 249		profile := tests.TestProfile{
 250			ID: uuid.New(),
 251			UserID: user.ID,
 252			Bio: "Test bio",
 253			Website: "https://example.com",
 254		}
 255	
 256		err := repo.Create(ctx, user)
 257		if err != nil {
 258			t.Fatalf("Failed to create test user: %v", err)
 259		}
 260	
 261		// Create profile separately
 262		err = db.Create(&profile).Error
 263		if err != nil {
 264			t.Fatalf("Failed to create test profile: %v", err)
 265		}
 266	
 267		// Find user with profile preloaded
 268		foundUser, err := repo.FindById(ctx, user.ID, WithRelations("Profile"))
 269		if err != nil {
 270			t.Errorf("FindById with relations failed: %v", err)
 271		}
 272	
 273		if foundUser.Profile == nil {
 274			t.Error("Expected profile to be loaded, but it was nil")
 275		} else if foundUser.Profile.Bio != "Test bio" {
 276			t.Errorf("Expected profile bio 'Test bio', got %s", foundUser.Profile.Bio)
 277		}
 278	}
