 401	func TestGormRepository_Transaction_Finish_Success(t *testing.T) {
 402		db := setupTestDB(t)
 403		repo := &GormRepository[tests.TestUser]{DB: db}
 404		ctx := context.Background()
 405	
 406		var err error
 407		tx := repo.BeginTransaction()
 408		defer tx.Finish(&err)
 409	
 410		user := createTestUser()
 411		err = repo.Create(ctx, user, WithTx(tx))
 412		if err != nil {
 413			t.Errorf("Create in transaction failed: %v", err)
 414			return
 415		}
 416	
 417		// err is nil, so transaction should commit
 418		// Verify after defer executes by checking in a separate test
 419	}
