 444	func TestGormRepository_UpdateByIdWithMap(t *testing.T) {
 445		db := setupTestDB(t)
 446		repo := &GormRepository[tests.TestUser]{DB: db}
 447		ctx := context.Background()
 448	
 449		user := createTestUser()
 450		err := repo.Create(ctx, user)
 451		if err != nil {
 452			t.Fatalf("Failed to create test user: %v", err)
 453		}
 454	
 455		// Update using map
 456		updates := map[string]interface{}{
 457			"name": "Updated Name",
 458			"age": 35,
 459		}
 460	
 461		updatedUser, err := repo.UpdateByIdWithMap(ctx, user.ID, updates)
 462		if err != nil {
 463			t.Errorf("UpdateByIdWithMap failed: %v", err)
 464		}
 465	
 466		if updatedUser.Name != "Updated Name" {
 467			t.Errorf("Expected updated name 'Updated Name', got %s", updatedUser.Name)
 468		}
 469		if updatedUser.Age != 35 {
 470			t.Errorf("Expected updated age 35, got %d", updatedUser.Age)
 471		}
 472	}
