 177	func BenchmarkGormRepository_Transaction(b *testing.B) {
 178		db := setupBenchmarkDB(b)
 179		repo := &GormRepository[tests.TestUser]{DB: db}
 180		ctx := context.Background()
 181	
 182		b.ResetTimer()
 183		for i := 0; i < b.N; i++ {
 184			var err error
 185			tx := repo.BeginTransaction()
 186	
 187			user := tests.TestUser{
 188				ID: uuid.New(),
 189				Name: "Transaction User " + string(rune(i)),
 190				Email: "tx" + string(rune(i)) + "@example.com",
 191				Age: 25,
 192				Active: true,
 193			}
 194	
 195			err = repo.Create(ctx, user, WithTx(tx))
 196			if err != nil {
 197				tx.Rollback()
 198				b.Fatalf("Create in transaction failed: %v", err)
 199			}
 200	
 201			err = tx.Commit()
 202			if err != nil {
 203				b.Fatalf("Transaction commit failed: %v", err)
 204			}
 205		}
 206	}
