 227	func TestSuite_TransactionScenarios(t *testing.T) {
 228		db := tests.SetupTestDBWithConfig(t, tests.DefaultTestDBConfig())
 229		defer tests.CleanupTestDB(t, db)
 230	
 231		userRepo := &GormRepository[tests.TestUser]{DB: db}
 232		profileRepo := &GormRepository[tests.TestProfile]{DB: db}
 233		ctx := context.Background()
 234	
 235		t.Run("ComplexTransactionSuccess", func(t *testing.T) {
 236			var err error
 237			tx := userRepo.BeginTransaction()
 238			defer tx.Finish(&err)
 239	
 240			// Create user in transaction
 241			user := tests.NewTestUserBuilder().
 242				WithName("Transaction User").
 243				WithEmail("transaction@example.com").
 244				Build()
 245	
 246			err = userRepo.Create(ctx, user, WithTx(tx))
 247			if err != nil {
 248				t.Errorf("Failed to create user in transaction: %v", err)
 249				return
 250			}
 251	
 252			// Create profile in transaction
 253			profile := tests.NewTestProfileBuilder(user.ID).
 254				WithBio("Transaction profile bio").
 255				Build()
 256	
 257			err = profileRepo.Create(ctx, profile, WithTx(tx))
 258			if err != nil {
 259				t.Errorf("Failed to create profile in transaction: %v", err)
 260				return
 261			}
 262	
 263			// Transaction should commit automatically
 264		})
 265	
 266		// Verify both user and profile were created
 267		users, err := userRepo.FindMany(ctx, WithQuery(func(db *gorm.DB) *gorm.DB {
 268			return db.Where("email = ?", "transaction@example.com")
 269		}))
 270		if err != nil {
 271			t.Fatalf("Failed to find transaction user: %v", err)
 272		}
 273		if len(users) != 1 {
 274			t.Errorf("Expected 1 transaction user, got %d", len(users))
 275		}
 276	
 277		profiles, err := profileRepo.FindMany(ctx, WithQuery(func(db *gorm.DB) *gorm.DB {
 278			return db.Where("user_id = ?", users[0].ID)
 279		}))
 280		if err != nil {
 281			t.Fatalf("Failed to find transaction profile: %v", err)
 282		}
 283		if len(profiles) != 1 {
 284			t.Errorf("Expected 1 transaction profile, got %d", len(profiles))
 285		}
 286	
 287		t.Run("ComplexTransactionFailure", func(t *testing.T) {
 288			var err error
 289			tx := userRepo.BeginTransaction()
 290			defer tx.Finish(&err)
 291	
 292			// Create user in transaction
 293			user := tests.NewTestUserBuilder().
 294				WithName("Failed Transaction User").
 295				WithEmail("failed@example.com").
 296				Build()
 297	
 298			err = userRepo.Create(ctx, user, WithTx(tx))
 299			if err != nil {
 300				t.Errorf("Failed to create user in transaction: %v", err)
 301				return
 302			}
 303	
 304			// Simulate an error
 305			err = gorm.ErrInvalidTransaction
 306	
 307			// Transaction should rollback automatically
 308		})
 309	
 310		// Verify user was not created due to rollback
 311		failedUsers, err := userRepo.FindMany(ctx, WithQuery(func(db *gorm.DB) *gorm.DB {
 312			return db.Where("email = ?", "failed@example.com")
 313		}))
 314		if err != nil {
 315			t.Fatalf("Failed to search for failed transaction user: %v", err)
 316		}
 317		if len(failedUsers) != 0 {
 318			t.Errorf("Expected 0 failed transaction users, got %d", len(failedUsers))
 319		}
 320	}
