 243	func (r *GormRepository[T]) AppendAssociation(ctx context.Context, entity T, association string, values interface{}, options ...Option) error {
 244		return applyOptions(r.DB, options).
 245			WithContext(ctx).
 246			Model(&entity).
 247			Omit(association + ".*"). // https://gorm.io/docs/associations.html#Using-Omit-to-Exclude-Fields-or-Associations
 248			Association(association).
 249			Append(values)
 250	}
 251	
 252	func (r *GormRepository[T]) RemoveAssociation(ctx context.Context, entity T, association string, values interface{}, options ...Option) error {
 253		return applyOptions(r.DB, options).
 254			WithContext(ctx).
 255			Model(&entity).
 256			Association(association).
 257			Delete(values)
 258	}
 259	
 260	func (r *GormRepository[T]) ReplaceAssociation(ctx context.Context, entity T, association string, values interface{}, options ...Option) error {
 261		return applyOptions(r.DB, options).
 262			WithContext(ctx).
 263			Model(&entity).
 264			Omit(association + ".*").
 265			Association(association).
 266			Replace(values)
 267	}
 268	
 269	func (r *GormRepository[T]) GetDB() *gorm.DB {
 270		return r.DB
 271	}
 272	
 273	// BeginTransaction starts a new transaction that should be used with defer for automatic cleanup
 274	func (r *GormRepository[T]) BeginTransaction() *Tx {
 275		gtx := r.DB.Begin()
 276		return &Tx{
 277			gtx: gtx,
 278			committed: false,
 279			rolledBack: false,
 280			clonedEntities: make(map[string]interface{}),
 281		}
 282	}
 283	
 284	// WithTx returns an option to run the query within a transaction.
 285	// When used with Find operations, it automatically clones entities that support cloning.
 286	func WithTx(tx *Tx) Option {
 287		return func(db *gorm.DB) *gorm.DB {
 288			// Store the transaction reference in the context for later use
 289			return tx.gtx.Set(txContextKey, tx)
 290		}
 291	}
 292	
 293	// WithQuery returns an option to customize the query.
 294	func WithQuery(fn func(*gorm.DB) *gorm.DB) Option {
 295		return func(db *gorm.DB) *gorm.DB {
 296			return fn(db)
 297		}
 298	}
 299	
 300	func WithQueryStruct(query map[string]interface{}) Option {
 301		return func(db *gorm.DB) *gorm.DB {
 302			return db.Where(query)
 303		}
 304	}
 305	
 306	type Tx struct {
 307		gtx *gorm.DB
 308		committed bool
 309		rolledBack bool
 310		// clonedEntities stores cloned entities as snapshots during transaction
 311		// key is a unique identifier for the entity, value is the cloned entity snapshot
 312		clonedEntities map[string]interface{}
 313		mutex sync.RWMutex
 314	}
 315	
 316	// BeginTransaction starts a nested transaction
 317	func (tx *Tx) BeginTransaction() *Tx {
 318		gtx := tx.gtx.Begin()
 319		return &Tx{
 320			gtx: gtx,
 321			committed: false,
 322			rolledBack: false,
 323			clonedEntities: make(map[string]interface{}),
 324		}
 325	}
 326	
 327	// Commit commits the transaction
 328	func (tx *Tx) Commit() error {
 329		if tx.committed || tx.rolledBack {
 330			return nil
 331		}
 332	
 333		err := tx.gtx.Commit().Error
 334		if err == nil {
 335			tx.committed = true
 336		}
 337		return err
 338	}
 339	
 340	// Rollback rolls back the transaction
 341	func (tx *Tx) Rollback() error {
 342		if tx.committed || tx.rolledBack {
 343			return nil
 344		}
 345	
 346		err := tx.gtx.Rollback().Error
 347		if err == nil {
 348			tx.rolledBack = true
 349		}
 350		return err
 351	}
 352	
 353	// Finish should be called with defer to automatically handle commit/rollback
 354	// Usage: defer tx.Finish(&err)
 355	// Use this for simple cases where you don't need complex error handling
 356	// Will commit if err is nil, rollback if err is set
 357	func (tx *Tx) Finish(err *error) {
 358		if tx.committed || tx.rolledBack {
 359			return
 360		}
 361	
 362		if *err != nil {
 363			// If there was an error, rollback
 364			if rollbackErr := tx.Rollback(); rollbackErr != nil {
 365				// Log rollback error but don't override the original error
 366				// You might want to use your logging framework here
 367			}
 368		} else {
 369			// If no error, commit
 370			if commitErr := tx.Commit(); commitErr != nil {
 371				*err = commitErr
 372			}
 373		}
 374	}
 375	
 376	// Error returns any error from the underlying GORM transaction
 377	func (tx *Tx) Error() error {
 378		return tx.gtx.Error
 379	}
 380	
 381	// storeClonedEntity stores the original entity before cloning
 382	func (tx *Tx) storeClonedEntity(entityKey string, original interface{}) {
 383		tx.mutex.Lock()
 384		defer tx.mutex.Unlock()
 385		tx.clonedEntities[entityKey] = original
 386	}
 387	
 388	// getClonedEntity retrieves the original entity if it was cloned
 389	func (tx *Tx) getClonedEntity(entityKey string) (interface{}, bool) {
 390		tx.mutex.RLock()
 391		defer tx.mutex.RUnlock()
 392		original, exists := tx.clonedEntities[entityKey]
 393		return original, exists
 394	}
 395	
 396	// generateEntityKey creates a unique key for an entity based on its type and ID
 397	func generateEntityKey(entity interface{}) string {
 398		entityType := reflect.TypeOf(entity)
 399		if entityType.Kind() == reflect.Ptr {
 400			entityType = entityType.Elem()
 401		}
 402	
 403		// Try to get ID field using reflection
 404		entityValue := reflect.ValueOf(entity)
 405		if entityValue.Kind() == reflect.Ptr {
 406			entityValue = entityValue.Elem()
 407		}
 408	
 409		idField := entityValue.FieldByName("Id")
 410		if !idField.IsValid() {
 411			// Fallback to memory address if no ID field
 412			return fmt.Sprintf("%s_%p", entityType.Name(), entity)
 413		}
 414	
 415		return fmt.Sprintf("%s_%v", entityType.Name(), idField.Interface())
 416	}
 417	
 418	// storeCloneIfInTransaction stores a clone of the entity if we're in a transaction and the entity supports cloning
 419	func storeCloneIfInTransaction[T any](db *gorm.DB, entity T) {
 420		// Check if we're in a transaction context
 421		txInterface, exists := db.Get(txContextKey)
 422		if !exists {
 423			return
 424		}
 425	
 426		tx, ok := txInterface.(*Tx)
 427		if !ok {
 428			return
 429		}
 430	
 431		// Check if entity supports cloning
 432		cloneable, ok := any(entity).(Cloneable[T])
 433		if !ok {
 434			return
 435		}
 436	
 437		// Store the cloned entity as a snapshot
 438		entityKey := generateEntityKey(entity)
 439		clone := cloneable.Clone()
 440		tx.storeClonedEntity(entityKey, clone)
 441	}
