 320		err := repo.Create(ctx, user)
 321		if err != nil {
 322			t.Fatalf("Failed to create test user: %v", err)
 323		}
 324	
 325		// Find user using struct query
 326		foundUsers, err := repo.FindMany(ctx, WithQueryStruct(map[string]interface{}{
 327			"email": user.Email,
 328			"active": true,
 329		}))
 330		if err != nil {
 331			t.Errorf("FindMany with query struct failed: %v", err)
 332		}
