 380	func TestGormRepository_Transaction_Rollback(t *testing.T) {
 381		db := setupTestDB(t)
 382		repo := &GormRepository[tests.TestUser]{DB: db}
 383		ctx := context.Background()
 384	
 385		tx := repo.BeginTransaction()
 386	
 387		user := createTestUser()
 388		err := repo.Create(ctx, user, WithTx(tx))
 389		if err != nil {
 390			t.Errorf("Create in transaction failed: %v", err)
 391		}
 392	
 393		// Rollback the transaction
 394		err = tx.Rollback()
 395		if err != nil {
 396			t.Errorf("Transaction rollback failed: %v", err)
 397		}
 398	
 399		// Verify the user was not created
 400		var count int64
 401		db.Model(&tests.TestUser{}).Count(&count)
 402		if count != 0 {
 403			t.Errorf("Expected 0 users after rollback, got %d", count)
 404		}
 405	}
