 513	func TestGormRepository_AppendAssociation(t *testing.T) {
 514		db := setupIntegrationDB(t) // Use integration DB for associations
 515		userRepo := &GormRepository[tests.TestUser]{DB: db}
 516		postRepo := &GormRepository[tests.TestPost]{DB: db}
 517		ctx := context.Background()
 518	
 519		// Create user
 520		user := createTestUser()
 521		err := userRepo.Create(ctx, user)
 522		if err != nil {
 523			t.Fatalf("Failed to create test user: %v", err)
 524		}
 525	
 526		// Create posts
 527		post1 := tests.TestPost{
 528			ID: uuid.New(),
 529			UserID: user.ID,
 530			Title: "First Post",
 531			Content: "Content of first post",
 532			Published: true,
 533		}
 534		post2 := tests.TestPost{
 535			ID: uuid.New(),
 536			UserID: user.ID,
 537			Title: "Second Post",
 538			Content: "Content of second post",
 539			Published: false,
 540		}
 541	
 542		err = postRepo.Create(ctx, post1)
 543		if err != nil {
 544			t.Fatalf("Failed to create first post: %v", err)
 545		}
 546		err = postRepo.Create(ctx, post2)
 547		if err != nil {
 548			t.Fatalf("Failed to create second post: %v", err)
 549		}
 550	
 551		// Append posts to user
 552		err = userRepo.AppendAssociation(ctx, user, "Posts", []tests.TestPost{post1, post2})
 553		if err != nil {
 554			t.Errorf("AppendAssociation failed: %v", err)
 555		}
 556	
 557		// Verify associations
 558		foundUser, err := userRepo.FindById(ctx, user.ID, WithRelations("Posts"))
 559		if err != nil {
 560			t.Fatalf("Failed to find user with posts: %v", err)
 561		}
 562	
 563		if len(foundUser.Posts) != 2 {
 564			t.Errorf("Expected 2 posts, got %d", len(foundUser.Posts))
 565		}
 566	}
 567	
 568	func TestGormRepository_RemoveAssociation(t *testing.T) {
 569		db := setupIntegrationDB(t)
 570		userRepo := &GormRepository[tests.TestUser]{DB: db}
 571		postRepo := &GormRepository[tests.TestPost]{DB: db}
 572		ctx := context.Background()
 573	
 574		// Create user and post
 575		user := createTestUser()
 576		err := userRepo.Create(ctx, user)
 577		if err != nil {
 578			t.Fatalf("Failed to create test user: %v", err)
 579		}
 580	
 581		post := tests.TestPost{
 582			ID: uuid.New(),
 583			UserID: user.ID,
 584			Title: "Test Post",
 585			Content: "Test content",
 586			Published: true,
 587		}
 588		err = postRepo.Create(ctx, post)
 589		if err != nil {
 590			t.Fatalf("Failed to create test post: %v", err)
 591		}
 592	
 593		// First append the association
 594		err = userRepo.AppendAssociation(ctx, user, "Posts", []tests.TestPost{post})
 595		if err != nil {
 596			t.Fatalf("Failed to append association: %v", err)
 597		}
 598	
 599		// Then remove it
 600		err = userRepo.RemoveAssociation(ctx, user, "Posts", []tests.TestPost{post})
 601		if err != nil {
 602			t.Errorf("RemoveAssociation failed: %v", err)
 603		}
 604	
 605		// Verify association was removed
 606		foundUser, err := userRepo.FindById(ctx, user.ID, WithRelations("Posts"))
 607		if err != nil {
 608			t.Fatalf("Failed to find user with posts: %v", err)
 609		}
 610	
 611		if len(foundUser.Posts) != 0 {
 612			t.Errorf("Expected 0 posts after removal, got %d", len(foundUser.Posts))
 613		}
 614	}
