 58	// FindPaginated retrieves records with pagination.
 59	func (r *GormRepository[T]) FindPaginated(ctx context.Context, page int, pageSize int, options ...Option) (*PaginationResult[T], error) {
 60		var entities []T
 61		var totalRows int64
 62	
 63		db := applyOptions(r.DB, options).WithContext(ctx)
 64		db.Model(&entities).Count(&totalRows)
 65	
 66		offset := (page - 1) * pageSize
 67		if err := db.Offset(offset).Limit(pageSize).Find(&entities).Error; err != nil {
 68			return nil, err
 69		}
 70	
 71		result := &PaginationResult[T]{
 72			Data: entities,
 73			Total: totalRows,
 74			Limit: pageSize,
 75			Offset: offset,
 76			CurrentPage: page,
 77			LastPage: int((totalRows + int64(pageSize) - 1) / int64(pageSize)),
 78			From: offset + 1,
 79			To: offset + len(entities),
 80		}
 81	
 82		return result, nil
 83	}
