 85	func (r *GormRepository[T]) FindOne(ctx context.Context, options ...Option) (T, error) {
 86		var entity T
 87		db := applyOptions(r.DB, options).WithContext(ctx)
 88	
 89		if err := db.First(&entity).Error; err != nil {
 90			return entity, err
 91		}
 92	
 93		// Store clone if in transaction and supports cloning
 94		storeCloneIfInTransaction(db, entity)
 95	
 96		return entity, nil
 97	}
