 99	func (r *GormRepository[T]) FindById(ctx context.Context, id uuid.UUID, options ...Option) (T, error) {
 100		var entity T
 101		db := applyOptions(r.DB, options).WithContext(ctx)
 102		if err := db.First(&entity, "id = ?", id).Error; err != nil {
 103			return entity, err
 104		}
 105	
 106		// Store clone if in transaction and supports cloning
 107		storeCloneIfInTransaction(db, entity)
 108	
 109		return entity, nil
 110	}
