 149	func (r *GormRepository[T]) UpdateById(ctx context.Context, id uuid.UUID, entity T, options ...Option) error {
 150		db := applyOptions(r.DB, options).WithContext(ctx)
 151	
 152		// Check if we're in a transaction context
 153		txInterface, exists := db.Get(txContextKey)
 154		if !exists {
 155			return fmt.Errorf("UpdateById requires a transaction context with a cloned entity - use WithTx option and ensure entity implements Cloneable[T] and Diffable[T] interfaces")
 156		}
 157	
 158		tx, ok := txInterface.(*Tx)
 159		if !ok {
 160			return fmt.Errorf("invalid transaction context")
 161		}
