 9	// Option represents a functional option for configuring the repository methods.
 10	type Option func(*gorm.DB) *gorm.DB
 11	
 12	type PaginationResult[T any] struct {
 13		Data []T `json:"data"`
 14		Total int64 `json:"total"`
 15		Limit int `json:"limit"`
 16		Offset int `json:"offset"`
 17		CurrentPage int `json:"currentPage"`
 18		LastPage int `json:"lastPage"`
 19		From int `json:"from"`
 20		To int `json:"to"`
 21	}
