 37		FindOne(ctx context.Context, options ...Option) (T, error)
 38		Create(ctx context.Context, entity T, options ...Option) error
 39		Save(ctx context.Context, entity T, options ...Option) error
 40		UpdateById(ctx context.Context, id uuid.UUID, entity T, options ...Option) error
 41		UpdateByIdWithMask(ctx context.Context, id uuid.UUID, mask map[string]interface{}, entity T, options ...Option) error
 42		UpdateByIdWithMap(ctx context.Context, id uuid.UUID, values map[string]interface{}, options ...Option) (T, error)
 43		UpdateByIdInPlace(ctx context.Context, id uuid.UUID, entity T, updateFunc func(T), options ...Option) error
 44		DeleteById(ctx context.Context, id uuid.UUID, options ...Option) error
 45		BeginTransaction() *Tx
 46		AppendAssociation(ctx context.Context, entity T, association string, values interface{}, options ...Option) error
 47		RemoveAssociation(ctx context.Context, entity T, association string, values interface{}, options ...Option) error
 48		ReplaceAssociation(ctx context.Context, entity T, association string, values interface{}, options ...Option) error
 49		GetDB() *gorm.DB
 50	}
