 235	func CreateTestUsers(t *testing.T, repo *GormRepository[TestUser], count int) []TestUser {
 236		t.Helper()
 237		ctx := context.Background()
 238		users := make([]TestUser, count)
 239	
 240		for i := 0; i < count; i++ {
 241			user := NewTestUserBuilder().
 242				WithName("Test User " + string(rune(i+'1'))).
 243				WithEmail("user" + string(rune(i+'1')) + "@example.com").
 244				WithAge(20 + i).
 245				WithActive(i%2 == 0).
 246				Build()
 247	
 248			err := repo.Create(ctx, user)
 249			if err != nil {
 250				t.Fatalf("Failed to create test user %d: %v", i, err)
 251			}
 252			users[i] = user
 253		}
 254	
 255		return users
 256	}
