 111	func TestIntegration_TransactionWorkflow(t *testing.T) {
 112		db := setupIntegrationDB(t)
 113		userRepo := &GormRepository[TestUser]{DB: db}
 114		ctx := context.Background()
 115	
 116		// Test successful transaction
 117		t.Run("Successful Transaction", func(t *testing.T) {
 118			var err error
 119			tx := userRepo.BeginTransaction()
 120			defer tx.Finish(&err)
 121	
 122			user1 := TestUser{
 123				ID: uuid.New(),
 124				Name: "Transaction User 1",
 125				Email: "tx1@example.com",
 126				Age: 25,
 127				Active: true,
 128			}
 129	
 130			user2 := TestUser{
 131				ID: uuid.New(),
 132				Name: "Transaction User 2",
 133				Email: "tx2@example.com",
 134				Age: 30,
 135				Active: true,
 136			}
 137	
 138			err = userRepo.Create(ctx, user1, WithTx(tx))
 139			if err != nil {
 140				t.Errorf("Failed to create user1 in transaction: %v", err)
 141				return
 142			}
 143	
 144			err = userRepo.Create(ctx, user2, WithTx(tx))
 145			if err != nil {
 146				t.Errorf("Failed to create user2 in transaction: %v", err)
 147				return
 148			}
 149	
 150			// Transaction should commit automatically via defer
 151		})
 152	
 153		// Verify both users were created
 154		users, err := userRepo.FindMany(ctx, WithQuery(func(db *gorm.DB) *gorm.DB {
 155			return db.Where("email IN ?", []string{"tx1@example.com", "tx2@example.com"})
 156		}))
 157		if err != nil {
 158			t.Fatalf("Failed to find transaction users: %v", err)
 159		}
 160	
 161		if len(users) != 2 {
 162			t.Errorf("Expected 2 users after successful transaction, got %d", len(users))
 163		}
 164	
 165		// Test failed transaction
 166		t.Run("Failed Transaction", func(t *testing.T) {
 167			var err error
 168			tx := userRepo.BeginTransaction()
 169			defer tx.Finish(&err)
 170	
 171			user3 := TestUser{
 172				ID: uuid.New(),
 173				Name: "Transaction User 3",
 174				Email: "tx3@example.com",
 175				Age: 35,
 176				Active: true,
 177			}
 178	
 179			err = userRepo.Create(ctx, user3, WithTx(tx))
 180			if err != nil {
 181				t.Errorf("Failed to create user3 in transaction: %v", err)
 182				return
 183			}
 184	
 185			// Simulate an error
 186			err = gorm.ErrInvalidTransaction
 187	
 188			// Transaction should rollback automatically via defer
 189		})
 190	
 191		// Verify user3 was not created due to rollback
 192		user3Count := int64(0)
 193		db.Model(&TestUser{}).Where("email = ?", "tx3@example.com").Count(&user3Count)
 194		if user3Count != 0 {
 195			t.Errorf("Expected 0 users with email tx3@example.com after rollback, got %d", user3Count)
 196		}
 197	}
