 496		if err != nil {
 497			t.Fatalf("Failed to find updated user: %v", err)
 498		}
 499	
 500		// Note: The in-place update modifies a copy, so original values should remain
 501		// This test verifies the method executes without error
 502		if updatedUser.ID != user.ID {
 503			t.Errorf("Expected user ID %v, got %v", user.ID, updatedUser.ID)
 504		}
