 184	func TestGormRepository_Save(t *testing.T) {
 185		db := setupTestDB(t)
 186		repo := &GormRepository[tests.TestUser]{DB: db}
 187		ctx := context.Background()
 188	
 189		user := createTestUser()
 190		err := repo.Create(ctx, user)
 191		if err != nil {
 192			t.Fatalf("Failed to create test user: %v", err)
 193		}
 194	
 195		// Update user
 196		user.Name = "Jane Doe"
 197		user.Age = 25
 198	
 199		err = repo.Save(ctx, user)
 200		if err != nil {
 201			t.Errorf("Save failed: %v", err)
 202		}
 203	
 204		// Verify the update
 205		updatedUser, err := repo.FindById(ctx, user.ID)
 206		if err != nil {
 207			t.Fatalf("Failed to find updated user: %v", err)
 208		}
 209	
 210		if updatedUser.Name != "Jane Doe" {
