 280	func TestGormRepository_WithQuery(t *testing.T) {
 281		db := setupTestDB(t)
 282		repo := &GormRepository[tests.TestUser]{DB: db}
 283		ctx := context.Background()
 284	
 285		// Create users with different ages
 286		users := []tests.TestUser{
 287			{ID: uuid.New(), Name: "Young User", Email: "young@example.com", Age: 20, Active: true},
 288			{ID: uuid.New(), Name: "Old User", Email: "old@example.com", Age: 50, Active: true},
 289		}
 290	
 291		for _, user := range users {
 292			err := repo.Create(ctx, user)
 293			if err != nil {
 294				t.Fatalf("Failed to create test user: %v", err)
 295			}
 296		}
 297	
 298		// Find users older than 30
 299		oldUsers, err := repo.FindMany(ctx, WithQuery(func(db *gorm.DB) *gorm.DB {
 300			return db.Where("age > ?", 30)
 301		}))
 302		if err != nil {
 303			t.Errorf("FindMany with query failed: %v", err)
 304		}
 305	
 306		if len(oldUsers) != 1 {
 307			t.Errorf("Expected 1 old user, got %d", len(oldUsers))
 308		}
 309		if oldUsers[0].Name != "Old User" {
 310			t.Errorf("Expected 'Old User', got %s", oldUsers[0].Name)
 311		}
 312	}
