 342	func TestGormRepository_Transaction_Commit(t *testing.T) {
 343		db := setupTestDB(t)
 344		repo := &GormRepository[tests.TestUser]{DB: db}
 345		ctx := context.Background()
 346	
 347		tx := repo.BeginTransaction()
 348		defer func() {
 349			if !tx.committed && !tx.rolledBack {
 350				tx.Rollback()
 351			}
 352		}()
 353	
 354		user := createTestUser()
 355		err := repo.Create(ctx, user, WithTx(tx))
 356		if err != nil {
 357			t.Errorf("Create in transaction failed: %v", err)
 358		}
 359	
 360		// Commit the transaction
 361		err = tx.Commit()
 362		if err != nil {
 363			t.Errorf("Transaction commit failed: %v", err)
 364		}
 365	
 366		// Verify the user was created
 367		var count int64
 368		db.Model(&tests.tests.TestUser{}).Count(&count)
 369		if count != 1 {
 370			t.Errorf("Expected 1 user after commit, got %d", count)
 371		}
 372	}
 373	
 374	func TestGormRepository_Transaction_Rollback(t *testing.T) {
 375		db := setupTestDB(t)
 376		repo := &GormRepository[tests.TestUser]{DB: db}
 377		ctx := context.Background()
 378	
 379		tx := repo.BeginTransaction()
 380	
 381		user := createTestUser()
 382		err := repo.Create(ctx, user, WithTx(tx))
 383		if err != nil {
 384			t.Errorf("Create in transaction failed: %v", err)
 385		}
 386	
 387		// Rollback the transaction
 388		err = tx.Rollback()
 389		if err != nil {
 390			t.Errorf("Transaction rollback failed: %v", err)
 391		}
 392	
 393		// Verify the user was not created
 394		var count int64
 395		db.Model(&tests.tests.TestUser{}).Count(&count)
 396		if count != 0 {
 397			t.Errorf("Expected 0 users after rollback, got %d", count)
 398		}
 399	}
