 235	// Createtests.TestUsers creates multiple test users in the database
 236	func Createtests.TestUsers(t *testing.T, repo *GormRepository[tests.TestUser], count int) []tests.TestUser {
 237		t.Helper()
 238		ctx := context.Background()
 239		users := make([]tests.TestUser, count)
 240	
 241		for i := 0; i < count; i++ {
 242			user := Newtests.TestUserBuilder().
 243				WithName("Test User " + string(rune(i+'1'))).
 244				WithEmail("user" + string(rune(i+'1')) + "@example.com").
 245				WithAge(20 + i).
 246				WithActive(i%2 == 0).
 247				Build()
 248	
 249			err := repo.Create(ctx, user)
 250			if err != nil {
 251				t.Fatalf("Failed to create test user %d: %v", i, err)
 252			}
 253			users[i] = user
 254		}
 255	
 256		return users
 257	}
