 26			profileClone := *u.Profile
 27			clone.Profile = &profileClone
 28		}
 29		if u.Posts != nil {
 30			clone.Posts = make([]TestPost, len(u.Posts))
 31			copy(clone.Posts, u.Posts)
 32		}
 33		return clone
 34	}
 35	
 36	// Diff implements the Diffable interface
 37	func (u TestUser) Diff(other TestUser) map[string]interface{} {
 38		diff := make(map[string]interface{})
 39	
 40		if u.Name != other.Name {
 41			diff["name"] = u.Name
 42		}
 43		if u.Email != other.Email {
 44			diff["email"] = u.Email
 45		}
 46		if u.Age != other.Age {
 47			diff["age"] = u.Age
 48		}
 49		if u.Active != other.Active {
 50			diff["active"] = u.Active
 51		}
 52	
 53		return diff
 54	}
 55	
 56	// TestProfile represents a user profile for testing relationships
 57	type TestProfile struct {
 58		ID uuid.UUID `gorm:"type:uuid;primary_key;default:gen_random_uuid()" json:"id"`
 59		UserID uuid.UUID `gorm:"type:uuid;not null" json:"userId"`
 60		Bio string `json:"bio"`
 61		Website string `json:"website"`
 62		Settings map[string]interface{} `gorm:"type:jsonb" json:"settings"`
 63	}
 64	
 65	// TestPost represents a blog post for testing one-to-many relationships
 66	type TestPost struct {
 67		ID uuid.UUID `gorm:"type:uuid;primary_key;default:gen_random_uuid()" json:"id"`
 68		UserID uuid.UUID `gorm:"type:uuid;not null" json:"userId"`
 69		Title string `gorm:"not null" json:"title"`
 70		Content string `json:"content"`
 71		Published bool `gorm:"default:false" json:"published"`
 72		Tags []TestTag `gorm:"many2many:post_tags;" json:"tags,omitempty"`
 73		CreatedAt time.Time `json:"createdAt"`
 74		UpdatedAt time.Time `json:"updatedAt"`
 75	}
 76	
 77	// TestTag represents a tag for testing many-to-many relationships
 78	type TestTag struct {
 79		ID uuid.UUID `gorm:"type:uuid;primary_key;default:gen_random_uuid()" json:"id"`
 80		Name string `gorm:"unique;not null" json:"name"`
 81		Posts []TestPost `gorm:"many2many:post_tags;" json:"posts,omitempty"`
 82	}
 83	
 84	// TestSimpleEntity represents a simple entity without relationships
 85	type TestSimpleEntity struct {
 86		ID uuid.UUID `gorm:"type:uuid;primary_key;default:gen_random_uuid()" json:"id"`
 87		Value string `json:"value"`
 88	}
 89	
 90	// Clone implements the Cloneable interface
 91	func (e TestSimpleEntity) Clone() TestSimpleEntity {
 92		return e
 93	}
 94	
 95	// Diff implements the Diffable interface
 96	func (e TestSimpleEntity) Diff(other TestSimpleEntity) map[string]interface{} {
 97		diff := make(map[string]interface{})
 98		if e.Value != other.Value {
 99			diff["value"] = e.Value
 100		}
 101		return diff
 102	}
