 185	func TestGormRepository_Save(t *testing.T) {
 186		db := setupTestDB(t)
 187		repo := &GormRepository[tests.TestUser]{DB: db}
 188		ctx := context.Background()
 189	
 190		user := createTestUser()
 191		err := repo.Create(ctx, user)
 192		if err != nil {
 193			t.Fatalf("Failed to create test user: %v", err)
 194		}
 195	
 196		// Update user
 197		user.Name = "Jane Doe"
 198		user.Age = 25
 199	
 200		err = repo.Save(ctx, user)
 201		if err != nil {
 202			t.Errorf("Save failed: %v", err)
 203		}
 204	
 205		// Verify the update
 206		updatedUser, err := repo.FindById(ctx, user.ID)
 207		if err != nil {
 208			t.Fatalf("Failed to find updated user: %v", err)
 209		}
 210	
 211		if updatedUser.Name != "Jane Doe" {
 212			t.Errorf("Expected updated name 'Jane Doe', got %s", updatedUser.Name)
 213		}
 214		if updatedUser.Age != 25 {
 215			t.Errorf("Expected updated age 25, got %d", updatedUser.Age)
 216		}
