 272		}
 273	
 274		if foundUser.Profile == nil {
 275			t.Error("Expected profile to be loaded, but it was nil")
 276		} else if foundUser.Profile.Bio != "Test bio" {
 277			t.Errorf("Expected profile bio 'Test bio', got %s", foundUser.Profile.Bio)
