 281	func TestGormRepository_WithQuery(t *testing.T) {
 282		db := setupTestDB(t)
 283		repo := &GormRepository[tests.TestUser]{DB: db}
 284		ctx := context.Background()
 285	
 286		// Create users with different ages
 287		users := []tests.TestUser{
 288			{ID: uuid.New(), Name: "Young User", Email: "young@example.com", Age: 20, Active: true},
 289			{ID: uuid.New(), Name: "Old User", Email: "old@example.com", Age: 50, Active: true},
 290		}
 291	
 292		for _, user := range users {
 293			err := repo.Create(ctx, user)
 294			if err != nil {
 295				t.Fatalf("Failed to create test user: %v", err)
 296			}
 297		}
 298	
 299		// Find users older than 30
 300		oldUsers, err := repo.FindMany(ctx, WithQuery(func(db *gorm.DB) *gorm.DB {
 301			return db.Where("age > ?", 30)
 302		}))
 303		if err != nil {
 304			t.Errorf("FindMany with query failed: %v", err)
 305		}
 306	
 307		if len(oldUsers) != 1 {
 308			t.Errorf("Expected 1 old user, got %d", len(oldUsers))
 309		}
 310		if oldUsers[0].Name != "Old User" {
 311			t.Errorf("Expected 'Old User', got %s", oldUsers[0].Name)
 312		}
 313	}
