 508	func TestGormRepository_AppendAssociation(t *testing.T) {
 509		db := setupIntegrationDB(t) // Use integration DB for associations
 510		userRepo := &GormRepository[tests.TestUser]{DB: db}
 511		postRepo := &GormRepository[tests.TestPost]{DB: db}
 512		ctx := context.Background()
 513	
 514		// Create user
 515		user := createTestUser()
 516		err := userRepo.Create(ctx, user)
 517		if err != nil {
 518			t.Fatalf("Failed to create test user: %v", err)
 519		}
 520	
 521		// Create posts
 522		post1 := tests.TestPost{
 523			ID: uuid.New(),
 524			UserID: user.ID,
 525			Title: "First Post",
 526			Content: "Content of first post",
 527			Published: true,
 528		}
 529		post2 := tests.TestPost{
 530			ID: uuid.New(),
 531			UserID: user.ID,
 532			Title: "Second Post",
 533			Content: "Content of second post",
 534			Published: false,
 535		}
 536	
 537		err = postRepo.Create(ctx, post1)
 538		if err != nil {
 539			t.Fatalf("Failed to create first post: %v", err)
 540		}
 541		err = postRepo.Create(ctx, post2)
 542		if err != nil {
 543			t.Fatalf("Failed to create second post: %v", err)
 544		}
 545	
 546		// Append posts to user
 547		err = userRepo.AppendAssociation(ctx, user, "Posts", []tests.TestPost{post1, post2})
 548		if err != nil {
 549			t.Errorf("AppendAssociation failed: %v", err)
 550		}
 551	
 552		// Verify associations
 553		foundUser, err := userRepo.FindById(ctx, user.ID, WithRelations("Posts"))
 554		if err != nil {
 555			t.Fatalf("Failed to find user with posts: %v", err)
 556		}
 557	
 558		if len(foundUser.Posts) != 2 {
 559			t.Errorf("Expected 2 posts, got %d", len(foundUser.Posts))
 560		}
 561	}
 562	
 563	func TestGormRepository_RemoveAssociation(t *testing.T) {
 564		db := setupIntegrationDB(t)
 565		userRepo := &GormRepository[tests.TestUser]{DB: db}
 566		postRepo := &GormRepository[tests.TestPost]{DB: db}
 567		ctx := context.Background()
 568	
 569		// Create user and post
 570		user := createTestUser()
 571		err := userRepo.Create(ctx, user)
 572		if err != nil {
 573			t.Fatalf("Failed to create test user: %v", err)
 574		}
 575	
 576		post := tests.TestPost{
 577			ID: uuid.New(),
 578			UserID: user.ID,
 579			Title: "Test Post",
 580			Content: "Test content",
 581			Published: true,
 582		}
 583		err = postRepo.Create(ctx, post)
 584		if err != nil {
 585			t.Fatalf("Failed to create test post: %v", err)
 586		}
 587	
 588		// First append the association
 589		err = userRepo.AppendAssociation(ctx, user, "Posts", []tests.TestPost{post})
 590		if err != nil {
 591			t.Fatalf("Failed to append association: %v", err)
 592		}
 593	
 594		// Then remove it
 595		err = userRepo.RemoveAssociation(ctx, user, "Posts", []tests.TestPost{post})
 596		if err != nil {
 597			t.Errorf("RemoveAssociation failed: %v", err)
 598		}
 599	
 600		// Verify association was removed
 601		foundUser, err := userRepo.FindById(ctx, user.ID, WithRelations("Posts"))
 602		if err != nil {
 603			t.Fatalf("Failed to find user with posts: %v", err)
 604		}
 605	
 606		if len(foundUser.Posts) != 0 {
 607			t.Errorf("Expected 0 posts after removal, got %d", len(foundUser.Posts))
 608		}
 609	}
