 332		foundUsers, err := repo.FindMany(ctx, WithQueryStruct(map[string]interface{}{
 333			"email": user.Email,
 334			"active": true,
 335		}))
 336		if err != nil {
 337			t.Errorf("FindMany with query struct failed: %v", err)
 338		}
 339	
 340		if len(foundUsers) != 1 {
 341			t.Errorf("Expected 1 user, got %d", len(foundUsers))
 342		}
 343		if foundUsers[0].ID != user.ID {
 344			t.Errorf("Expected user ID %v, got %v", user.ID, foundUsers[0].ID)
 345		}
 346	}
