 348	func TestGormRepository_Transaction_Commit(t *testing.T) {
 349		db := setupTestDB(t)
 350		repo := &GormRepository[tests.TestUser]{DB: db}
 351		ctx := context.Background()
 352	
 353		tx := repo.BeginTransaction()
 354		defer func() {
 355			if !tx.committed && !tx.rolledBack {
 356				tx.Rollback()
 357			}
 358		}()
 359	
 360		user := createTestUser()
 361		err := repo.Create(ctx, user, WithTx(tx))
 362		if err != nil {
 363			t.Errorf("Create in transaction failed: %v", err)
 364		}
 365	
 366		// Commit the transaction
 367		err = tx.Commit()
 368		if err != nil {
 369			t.Errorf("Transaction commit failed: %v", err)
 370		}
 371	
 372		// Verify the user was created
 373		var count int64
 374		db.Model(&tests.TestUser{}).Count(&count)
 375		if count != 1 {
 376			t.Errorf("Expected 1 user after commit, got %d", count)
 377		}
 378	}
