 513	func TestGormRepository_AppendAssociation(t *testing.T) {
 514		db := setupIntegrationDB(t) // Use integration DB for associations
 515		userRepo := &GormRepository[tests.TestUser]{DB: db}
 516		postRepo := &GormRepository[tests.TestPost]{DB: db}
 517		ctx := context.Background()
 518	
 519		// Create user
 520		user := createTestUser()
 521		err := userRepo.Create(ctx, user)
 522		if err != nil {
 523			t.Fatalf("Failed to create test user: %v", err)
 524		}
 525	
 526		// Create posts
 527		post1 := tests.TestPost{
 528			ID: uuid.New(),
 529			UserID: user.ID,
 530			Title: "First Post",
 531			Content: "Content of first post",
 532			Published: true,
 533		}
 534		post2 := tests.TestPost{
 535			ID: uuid.New(),
 536			UserID: user.ID,
 537			Title: "Second Post",
 538			Content: "Content of second post",
 539			Published: false,
 540		}
 541	
 542		err = postRepo.Create(ctx, post1)
 543		if err != nil {
 544			t.Fatalf("Failed to create first post: %v", err)
 545		}
 546		err = postRepo.Create(ctx, post2)
 547		if err != nil {
 548			t.Fatalf("Failed to create second post: %v", err)
 549		}
 550	
 551		// Append posts to user
 552		err = userRepo.AppendAssociation(ctx, user, "Posts", []tests.TestPost{post1, post2})
 553		if err != nil {
 554			t.Errorf("AppendAssociation failed: %v", err)
 555		}
 556	
 557		// Verify associations
 558		foundUser, err := userRepo.FindById(ctx, user.ID, WithRelations("Posts"))
 559		if err != nil {
 560			t.Fatalf("Failed to find user with posts: %v", err)
 561		}
 562	
 563		if len(foundUser.Posts) != 2 {
 564			t.Errorf("Expected 2 posts, got %d", len(foundUser.Posts))
 565		}
 566	}
