 568	func TestGormRepository_RemoveAssociation(t *testing.T) {
 569		db := setupIntegrationDB(t)
 570		userRepo := &GormRepository[tests.TestUser]{DB: db}
 571		postRepo := &GormRepository[tests.TestPost]{DB: db}
 572		ctx := context.Background()
 573	
 574		// Create user and post
 575		user := createTestUser()
 576		err := userRepo.Create(ctx, user)
 577		if err != nil {
 578			t.Fatalf("Failed to create test user: %v", err)
 579		}
 580	
 581		post := tests.TestPost{
 582			ID: uuid.New(),
 583			UserID: user.ID,
 584			Title: "Test Post",
 585			Content: "Test content",
 586			Published: true,
 587		}
 588		err = postRepo.Create(ctx, post)
 589		if err != nil {
 590			t.Fatalf("Failed to create test post: %v", err)
 591		}
 592	
 593		// First append the association
 594		err = userRepo.AppendAssociation(ctx, user, "Posts", []tests.TestPost{post})
 595		if err != nil {
 596			t.Fatalf("Failed to append association: %v", err)
 597		}
 598	
 599		// Then remove it
 600		err = userRepo.RemoveAssociation(ctx, user, "Posts", []tests.TestPost{post})
 601		if err != nil {
 602			t.Errorf("RemoveAssociation failed: %v", err)
 603		}
 604	
 605		// Verify association was removed
 606		foundUser, err := userRepo.FindById(ctx, user.ID, WithRelations("Posts"))
 607		if err != nil {
 608			t.Fatalf("Failed to find user with posts: %v", err)
 609		}
 610	
 611		if len(foundUser.Posts) != 0 {
 612			t.Errorf("Expected 0 posts after removal, got %d", len(foundUser.Posts))
 613		}
 614	}
