 216		t.Helper()
 217	
 218		if expected.ID != actual.ID {
 219			t.Errorf("%s: ID mismatch - expected %v, got %v", message, expected.ID, actual.ID)
 220		}
 221		if expected.Name != actual.Name {
 222			t.Errorf("%s: Name mismatch - expected %s, got %s", message, expected.Name, actual.Name)
 223		}
 224		if expected.Email != actual.Email {
 225			t.Errorf("%s: Email mismatch - expected %s, got %s", message, expected.Email, actual.Email)
 226		}
 227		if expected.Age != actual.Age {
 228			t.Errorf("%s: Age mismatch - expected %d, got %d", message, expected.Age, actual.Age)
 229		}
 230		if expected.Active != actual.Active {
 231			t.Errorf("%s: Active mismatch - expected %t, got %t", message, expected.Active, actual.Active)
 232		}
 233	}
 234	
 235	// CreateTestUsers creates multiple test users in the database
 236	func CreateTestUsers(t *testing.T, repo *repositories.GormRepository[TestUser], count int) []TestUser {
 237		t.Helper()
 238		ctx := context.Background()
 239		users := make([]TestUser, count)
 240	
 241		for i := 0; i < count; i++ {
 242			user := NewTestUserBuilder().
 243				WithName("Test User " + string(rune(i+'1'))).
 244				WithEmail("user" + string(rune(i+'1')) + "@example.com").
 245				WithAge(20 + i).
 246				WithActive(i%2 == 0).
 247				Build()
 248	
 249			err := repo.Create(ctx, user)
 250			if err != nil {
 251				t.Fatalf("Failed to create test user %d: %v", i, err)
 252			}
 253			users[i] = user
 254		}
 255	
 256		return users
 257	}
 258	
 259	// CleanupTestDB removes all data from test tables
 260	func CleanupTestDB(t *testing.T, db *gorm.DB) {
 261		t.Helper()
 262	
 263		// Delete in reverse order of dependencies
 264		db.Exec("DELETE FROM post_tags")
 265		db.Exec("DELETE FROM test_posts")
 266		db.Exec("DELETE FROM test_tags")
 267		db.Exec("DELETE FROM test_profiles")
 268		db.Exec("DELETE FROM test_users")
 269		db.Exec("DELETE FROM test_simple_entities")
 270	}
 271	
 272	// AssertPaginationResult validates pagination result structure
 273	func AssertPaginationResult(t *testing.T, result *repositories.PaginationResult[TestUser], expectedTotal int64, expectedPage int, expectedPageSize int, message string) {
 274		t.Helper()
 275	
 276		if result.Total != expectedTotal {
 277			t.Errorf("%s: Total mismatch - expected %d, got %d", message, expectedTotal, result.Total)
 278		}
 279		if result.CurrentPage != expectedPage {
 280			t.Errorf("%s: CurrentPage mismatch - expected %d, got %d", message, expectedPage, result.CurrentPage)
 281		}
 282		if result.Limit != expectedPageSize {
 283			t.Errorf("%s: Limit mismatch - expected %d, got %d", message, expectedPageSize, result.Limit)
 284		}
 285	
 286		expectedLastPage := int((expectedTotal + int64(expectedPageSize) - 1) / int64(expectedPageSize))
 287		if expectedTotal == 0 {
 288			expectedLastPage = 0
 289		}
 290		if result.LastPage != expectedLastPage {
 291			t.Errorf("%s: LastPage mismatch - expected %d, got %d", message, expectedLastPage, result.LastPage)
 292		}
 293	}
