 220	func TestSuite_TransactionScenarios(t *testing.T) {
 221		db := SetupTestDBWithConfig(t, DefaultTestDBConfig())
 222		defer CleanupTestDB(t, db)
 223	
 224		userRepo := &repositories.GormRepository[TestUser]{DB: db}
 225		profileRepo := &repositories.GormRepository[TestProfile]{DB: db}
 226		ctx := context.Background()
 227	
 228		t.Run("ComplexTransactionSuccess", func(t *testing.T) {
 229			var err error
 230			tx := userRepo.BeginTransaction()
 231			defer tx.Finish(&err)
 232	
 233			// Create user in transaction
 234			user := NewTestUserBuilder().
 235				WithName("Transaction User").
 236				WithEmail("transaction@example.com").
 237				Build()
 238	
 239			err = userRepo.Create(ctx, user, repositories.WithTx(tx))
 240			if err != nil {
 241				t.Errorf("Failed to create user in transaction: %v", err)
 242				return
 243			}
 244	
 245			// Create profile in transaction
 246			profile := NewTestProfileBuilder(user.ID).
 247				WithBio("Transaction profile bio").
 248				Build()
 249	
 250			err = profileRepo.Create(ctx, profile, repositories.WithTx(tx))
 251			if err != nil {
 252				t.Errorf("Failed to create profile in transaction: %v", err)
 253				return
 254			}
 255	
 256			// Transaction should commit automatically
 257		})
 258	
 259		// Verify both user and profile were created
 260		users, err := userRepo.FindMany(ctx, repositories.WithQuery(func(db *gorm.DB) *gorm.DB {
 261			return db.Where("email = ?", "transaction@example.com")
 262		}))
 263		if err != nil {
 264			t.Fatalf("Failed to find transaction user: %v", err)
 265		}
 266		if len(users) != 1 {
 267			t.Errorf("Expected 1 transaction user, got %d", len(users))
 268		}
 269	
 270		profiles, err := profileRepo.FindMany(ctx, repositories.WithQuery(func(db *gorm.DB) *gorm.DB {
 271			return db.Where("user_id = ?", users[0].ID)
 272		}))
 273		if err != nil {
 274			t.Fatalf("Failed to find transaction profile: %v", err)
 275		}
 276		if len(profiles) != 1 {
 277			t.Errorf("Expected 1 transaction profile, got %d", len(profiles))
 278		}
 279	
 280		t.Run("ComplexTransactionFailure", func(t *testing.T) {
 281			var err error
 282			tx := userRepo.BeginTransaction()
 283			defer tx.Finish(&err)
 284	
 285			// Create user in transaction
 286			user := NewTestUserBuilder().
 287				WithName("Failed Transaction User").
 288				WithEmail("failed@example.com").
 289				Build()
 290	
 291			err = userRepo.Create(ctx, user, repositories.WithTx(tx))
 292			if err != nil {
 293				t.Errorf("Failed to create user in transaction: %v", err)
 294				return
 295			}
 296	
 297			// Simulate an error
 298			err = gorm.ErrInvalidTransaction
 299	
 300			// Transaction should rollback automatically
 301		})
 302	
 303		// Verify user was not created due to rollback
 304		failedUsers, err := userRepo.FindMany(ctx, repositories.WithQuery(func(db *gorm.DB) *gorm.DB {
 305			return db.Where("email = ?", "failed@example.com")
 306		}))
 307		if err != nil {
 308			t.Fatalf("Failed to search for failed transaction user: %v", err)
 309		}
 310		if len(failedUsers) != 0 {
 311			t.Errorf("Expected 0 failed transaction users, got %d", len(failedUsers))
 312		}
 313	}
