 1	package repositories
 2	
 3	import (
 4		"context"
 5		"fmt"
 6		"reflect"
 7		"sync"
 8		
 9		"github.com/google/uuid"
 10		"gorm.io/gorm"
 11		"gorm.io/gorm/clause"
 12	
 13		"github.com/ikateclab/gorm-repository/utils"
 14	)
 15	
 16	const (
 17		txContextKey = "__tx"
 18	)
 19	
 20	type GormRepository[T any] struct {
 21		Repository[T]
 22		DB *gorm.DB
 23	}
 24	
 25	func WithRelations(relations ...string) Option {
 26		return func(db *gorm.DB) *gorm.DB {
 27			for _, relation := range relations {
 28				db = db.Preload(relation)
 29			}
 30			return db
 31		}
 32	}
 33	
 34	func applyOptions(db *gorm.DB, options []Option) *gorm.DB {
 35		for _, option := range options {
 36			if option != nil {
 37				db = option(db)
 38			}
 39		}
 40		return db
 41	}
 42	
 43	func newEntity[T any]() T {
 44		var entity T
 45		entityType := reflect.TypeOf(entity)
 46		if entityType.Kind() == reflect.Ptr {
 47			return reflect.New(entityType.Elem()).Interface().(T)
 48		}
 49		return entity
 50	}
 51	
 52	func (r *GormRepository[T]) FindMany(ctx context.Context, options ...Option) ([]T, error) {
 53		var entities []T
 54		db := applyOptions(r.DB, options).WithContext(ctx)
 55		if err := db.Find(&entities).Error; err != nil {
 56			return nil, err
 57		}
 58		return entities, nil
 59	}
 60	
 61	// FindPaginated retrieves records with pagination.
 62	func (r *GormRepository[T]) FindPaginated(ctx context.Context, page int, pageSize int, options ...Option) (*PaginationResult[T], error) {
 63		var entities []T
 64		var totalRows int64
 65	
 66		db := applyOptions(r.DB, options).WithContext(ctx)
 67		db.Model(&entities).Count(&totalRows)
 68	
 69		offset := (page - 1) * pageSize
 70		if err := db.Offset(offset).Limit(pageSize).Find(&entities).Error; err != nil {
 71			return nil, err
 72		}
 73	
 74		result := &PaginationResult[T]{
 75			Data: entities,
 76			Total: totalRows,
 77			Limit: pageSize,
 78			Offset: offset,
 79			CurrentPage: page,
 80			LastPage: int((totalRows + int64(pageSize) - 1) / int64(pageSize)),
 81			From: offset + 1,
 82			To: offset + len(entities),
 83		}
 84	
 85		return result, nil
 86	}
 87	
 88	func (r *GormRepository[T]) FindOne(ctx context.Context, options ...Option) (T, error) {
 89		var entity T
 90		db := applyOptions(r.DB, options).WithContext(ctx)
 91	
 92		if err := db.First(&entity).Error; err != nil {
 93			return entity, err
 94		}
 95	
 96		// Store clone if in transaction and supports cloning
 97		storeCloneIfInTransaction(db, entity)
 98	
 99		return entity, nil
 100	}
 101	
 102	func (r *GormRepository[T]) FindById(ctx context.Context, id uuid.UUID, options ...Option) (T, error) {
 103		var entity T
 104		db := applyOptions(r.DB, options).WithContext(ctx)
 105		if err := db.First(&entity, "id = ?", id).Error; err != nil {
 106			return entity, err
 107		}
 108	
 109		// Store clone if in transaction and supports cloning
 110		storeCloneIfInTransaction(db, entity)
 111	
 112		return entity, nil
 113	}
 114	
 115	func (r *GormRepository[T]) Create(ctx context.Context, entity T, options ...Option) error {
 116		db := applyOptions(r.DB, options).WithContext(ctx)
 117		if err := db.Create(&entity).Error; err != nil {
 118			return err
 119		}
 120	
 121		storeCloneIfInTransaction(db, entity)
 122	
 123		return nil
 124	}
 125	
 126	func (r *GormRepository[T]) Save(ctx context.Context, entity T, options ...Option) error {
 127		db := applyOptions(r.DB, options).WithContext(ctx)
 128		return db.Save(&entity).Error
 129	}
...
 200	
 201	func (r *GormRepository[T]) UpdateByIdInPlace(ctx context.Context, id uuid.UUID, entity T, updateFunc func(T), options ...Option) error {
 202		db := applyOptions(r.DB, options).WithContext(ctx)
 203	
 204		// Check if entity supports cloning and diffing
 205		cloneable, isCloneable := any(entity).(Cloneable[T])
 206		if !isCloneable {
 207			return fmt.Errorf("entity does not support cloning - entity must implement Cloneable[T] interface")
 208		}
 209	
 210		_, isDiffable := any(entity).(Diffable[T])
 211		if !isDiffable {
 212			return fmt.Errorf("entity does not support diffing - entity must implement Diffable[T] interface")
 213		}
...
 237	
 238	func (r *GormRepository[T]) DeleteById(ctx context.Context, id uuid.UUID, options ...Option) error {
 239		db := applyOptions(r.DB, options).WithContext(ctx)
 240		return db.Delete(new(T), "id = ?", id).Error
 241	}
 242	
 243	func (r *GormRepository[T]) AppendAssociation(ctx context.Context, entity T, association string, values interface{}, options ...Option) error {
 244		return applyOptions(r.DB, options).
 245			WithContext(ctx).
 246			Model(&entity).
 247			Omit(association + ".*"). // https://gorm.io/docs/associations.html#Using-Omit-to-Exclude-Fields-or-Associations
 248			Association(association).
 249			Append(values)
 250	}
