 83				Build()
 84	
 85			err := userRepo.Create(ctx, user)
 86			if err != nil {
 87				t.Fatalf("Failed to create user: %v", err)
 88			}
 89	
 90			profile := NewTestProfileBuilder(user.ID).
 91				WithBio("Test bio for profile integration").
 92				WithWebsite("https://profile.example.com").
 93				WithSettings(`{"theme":"dark","language":"en","notifications":{"email":true,"push":false}}`).
 94				Build()
 95	
 96			err = profileRepo.Create(ctx, profile)
 97			if err != nil {
 98				t.Fatalf("Failed to create profile: %v", err)
 99			}
 100	
 101			// Find user with profile
 102			foundUser, err := userRepo.FindById(ctx, user.ID, repositories.WithRelations("Profile"))
 103			if err != nil {
 104				t.Fatalf("Failed to find user with profile: %v", err)
 105			}
 106	
 107			if foundUser.Profile == nil {
 108				t.Fatal("Expected profile to be loaded")
 109			}
 110			if foundUser.Profile.Bio != profile.Bio {
 111				t.Errorf("Expected profile bio %s, got %s", profile.Bio, foundUser.Profile.Bio)
 112			}
 113			if foundUser.Profile.Website != profile.Website {
 114				t.Errorf("Expected profile website %s, got %s", profile.Website, foundUser.Profile.Website)
 115			}
 116		})
 117	}
