 119	func TestSuite_PostsAndTags(t *testing.T) {
 120		db := SetupTestDBWithConfig(t, DefaultTestDBConfig())
 121		defer CleanupTestDB(t, db)
 122	
 123		userRepo := &repositories.GormRepository[TestUser]{DB: db}
 124		postRepo := &repositories.GormRepository[TestPost]{DB: db}
 125		tagRepo := &repositories.GormRepository[TestTag]{DB: db}
 126		ctx := context.Background()
 127	
 128		t.Run("UserWithPostsAndTags", func(t *testing.T) {
 129			user := NewTestUserBuilder().
 130				WithName("Blogger User").
 131				WithEmail("blogger@example.com").
 132				Build()
 133	
 134			err := userRepo.Create(ctx, user)
 135			if err != nil {
 136				t.Fatalf("Failed to create user: %v", err)
 137			}
 138	
 139			// Create tags
 140			tag1 := TestTag{ID: uuid.New(), Name: "Go"}
 141			tag2 := TestTag{ID: uuid.New(), Name: "Testing"}
 142			tag3 := TestTag{ID: uuid.New(), Name: "GORM"}
 143	
 144			for _, tag := range []TestTag{tag1, tag2, tag3} {
 145				err = tagRepo.Create(ctx, tag)
 146				if err != nil {
 147					t.Fatalf("Failed to create tag %s: %v", tag.Name, err)
 148				}
 149			}
 150	
 151			// Create posts
 152			post1 := NewTestPostBuilder(user.ID).
 153				WithTitle("Introduction to Go").
 154				WithContent("Go is a great programming language...").
 155				WithPublished(true).
 156				Build()
 157	
 158			post2 := NewTestPostBuilder(user.ID).
 159				WithTitle("Testing with GORM").
 160				WithContent("GORM makes database testing easier...").
 161				WithPublished(false).
 162				Build()
 163	
 164			err = postRepo.Create(ctx, post1)
 165			if err != nil {
 166				t.Fatalf("Failed to create post1: %v", err)
 167			}
 168			err = postRepo.Create(ctx, post2)
 169			if err != nil {
 170				t.Fatalf("Failed to create post2: %v", err)
 171			}
 172	
 173			// Associate tags with posts
 174			err = postRepo.AppendAssociation(ctx, post1, "Tags", []TestTag{tag1, tag2})
 175			if err != nil {
 176				t.Fatalf("Failed to associate tags with post1: %v", err)
 177			}
 178	
 179			err = postRepo.AppendAssociation(ctx, post2, "Tags", []TestTag{tag2, tag3})
 180			if err != nil {
 181				t.Fatalf("Failed to associate tags with post2: %v", err)
 182			}
 183	
 184			// Find user with posts and their tags
 185			foundUser, err := userRepo.FindById(ctx, user.ID, repositories.WithRelations("Posts", "Posts.Tags"))
 186			if err != nil {
 187				t.Fatalf("Failed to find user with posts and tags: %v", err)
 188			}
 189	
 190			if len(foundUser.Posts) != 2 {
 191				t.Errorf("Expected 2 posts, got %d", len(foundUser.Posts))
 192			}
 193	
 194			// Check that posts have tags
 195			for _, post := range foundUser.Posts {
 196				if len(post.Tags) == 0 {
 197					t.Errorf("Expected post %s to have tags", post.Title)
 198				}
 199			}
 200	
 201			// Find published posts only
 202			publishedPosts, err := postRepo.FindMany(ctx,
 203				repositories.WithQuery(func(db *gorm.DB) *gorm.DB {
 204					return db.Where("user_id = ? AND published = ?", user.ID, true)
 205				}),
 206				repositories.WithRelations("Tags"),
 207			)
 208			if err != nil {
 209				t.Fatalf("Failed to find published posts: %v", err)
 210			}
 211	
 212			if len(publishedPosts) != 1 {
 213				t.Errorf("Expected 1 published post, got %d", len(publishedPosts))
 214			}
 215			if publishedPosts[0].Title != "Introduction to Go" {
 216				t.Errorf("Expected published post title 'Introduction to Go', got %s", publishedPosts[0].Title)
 217			}
 218		})
 219	}
