 70	func TestSuite_ProfileIntegration(t *testing.T) {
 71		db := SetupTestDBWithConfig(t, DefaultTestDBConfig())
 72		defer CleanupTestDB(t, db)
 73	
 74		userRepo := &repositories.GormRepository[TestUser]{DB: db}
 75		profileRepo := &repositories.GormRepository[TestProfile]{DB: db}
 76		ctx := context.Background()
 77	
 78		t.Run("UserWithProfile", func(t *testing.T) {
 79			user := NewTestUserBuilder().
 80				WithName("Profile User").
 81				WithEmail("profile@example.com").
 82				Build()
 83	
 84			err := userRepo.Create(ctx, user)
 85			if err != nil {
 86				t.Fatalf("Failed to create user: %v", err)
 87			}
 88	
 89			profile := NewTestProfileBuilder(user.ID).
 90				WithBio("Test bio for profile integration").
 91				WithWebsite("https://profile.example.com").
 92				WithSettings(`{"theme":"dark","language":"en","notifications":{"email":true,"push":false}}`).
 93				Build()
 94	
 95			err = profileRepo.Create(ctx, profile)
 96			if err != nil {
 97				t.Fatalf("Failed to create profile: %v", err)
 98			}
 99	
 100			// Find user with profile
 101			foundUser, err := userRepo.FindById(ctx, user.ID, repositories.WithRelations("Profile"))
 102			if err != nil {
 103				t.Fatalf("Failed to find user with profile: %v", err)
 104			}
